import React from 'react'
import Slider from 'react-slick';
import { testimonials } from '../global/Data'

function Testimonials() {
    const settings = {
        dots: false,
        infinite: false,
        slidesToShow: 1,
        slidesToScroll: 1,
        variableWidth: true,
        //afterChange: (index) => setIndexSlick(index),
    };
    return (
        <section className='my-[30px] lg:mt-[100px]'>
            <div className='container mx-auto'>
                <div className='flex flex-col items-center justify-center'>
                    <div className='font-bold text-3xl max-w-sm text-center mb-8'>
                        {testimonials.title}
                    </div>
                    <div className='max-w-md text-center'>
                        {testimonials.subtitle}
                    </div>
                </div>
                <div className='flex items-center justify-center mt-10'>
                    <Slider {...settings}>
                            {testimonials.content.map((val, index) => {
                                return (
                                    <div key={index} className='border border-red-100 rounded-lg p-5 w-96 h-52'>
                                        {val.nama}
                                    </div>
                                )
                            })}


                    </Slider>
                </div>

            </div>
        </section>
    )
}

export default Testimonials
