import React from 'react'
import Slider from 'react-slick';
import { testimonials } from '../global/Data'

function Testimonials() {
    const settings = {
        dots: true,
        infinite: false,
        slidesToShow: 1,
        slidesToScroll: 1,
        variableWidth: true,
        //afterChange: (index) => setIndexSlick(index),
    };
    return (
        <section className='my-[30px] lg:mt-[100px]'>
            <div className='container mx-auto'>
                <div className='flex flex-col items-center justify-center'>
                    <div className='font-bold text-3xl max-w-sm text-center mb-8'>
                        {testimonials.title}
                    </div>
                    <div className='max-w-md text-center'>
                        {testimonials.subtitle}
                    </div>
                </div>
                <div className='mt-10'>
                    <Slider {...settings}>
                        {testimonials.content.map((val, index) => {
                            return (
                                <div key={index} className={index === 0 ? "ml-28" : ""}>
                                    <div className='border border-red-100 rounded-lg p-5 w-96 h-52 ml-6'>
                                        <div className='flex flex-row items-center justify-between'>
                                            <img src={val.img} alt="" className='mr-3' />
                                            <div className='flex-1'>
                                                <div className='flex flex-col'>
                                                    <div>{val.nama}</div>
                                                    <div className='text-gray-400 text-sm'>{val.loc}</div>
                                                </div>
                                            </div>
                                            <div className='flex flex-row items-center'>
                                                <div className='mr-2'>{val.rating}</div>
                                                <img src={testimonials.star} alt="" className='w-5 h-5' />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )
                        })}
                    </Slider>
                </div>

            </div>
        </section>
    )
}

export default Testimonials
