import React from 'react'
import { featuresSection } from '../global/Data'

function Features() {
    return (
        <section className='my-[30px] lg:mt-[100px]'
            data-aos='fade-left'
            data-aos-delay='400'
        >
            <div className="container mx-auto">
                <div className=' row-auto px-20'>
                    <div className=' col-span-6'>
                        <img src={featuresSection.img} alt="" />
                    </div>
                    <div className=' col-span-6'>
                        {featuresSection.content.map((item, idx)=>{
                            return (
                                <div className='flex flex-row space-x-3'>
                                    <div><img src={featuresSection.icon} /></div>
                                    <div>{item}</div>
                                </div>    
                            )
                        })}
                    </div>
                </div>
            </div>
        </section>
    )
}

export default Features
