import React from 'react'
import { secondSection } from '../global/Data'

function About() {
    return (
        <section
            className='my-[30px] lg:mt-[100px]'
            data-aos='fade-left'
            data-aos-delay='400'
        >
            <div className='container mx-auto px-20'>

                {/* Each About */}
                <div className='items-center'>
                    <div className='flex flex-col lg:grid lg:grid-cols-3 rounded-lg py-5 shadow-md'>
                        {secondSection.map((item, idx) => {
                            return (
                                <div key={idx} className="flex flex-row text-left py-5 px-20 lg:flex lg:flex-row space-x-6 lg:border-r lg:border-gray-200 lg:items-center lg:justify-center">
                                    <img src={item.icon} alt="" />
                                    <div>
                                        <div className='font-bold'>{item.title}</div>
                                        <div className="text-gray-500">{item.subtitle}</div>
                                    </div>
                                </div>
                            )
                        })}
                    </div>
                </div>
            </div>
        </section>
    )
}

export default About
